-- Função para enviar mensagem ao Discord
AddEventHandler("Discord", function(Hook, Message, Color)
    local webhook = Discords[Hook]
    if webhook then  -- Verifica se o webhook existe na tabela Discords
        PerformHttpRequest(webhook, function(err, text, headers) 
            if err ~= 200 then
                print("Erro ao enviar mensagem para o webhook:", err) -- Log de erro
            end
        end, "POST", json.encode({
            username = ServerName,
            embeds = {
                { 
                    color = Color, 
                    description = Message 
                }
            }
        }), { ["Content-Type"] = "application/json" })
    else
        print("Webhook não encontrado para o tipo:", Hook)
    end
end)
